//custom navbar
//shows login/signup if the user is logged in i.e. if the token is saved
//otherwise, shows logout
//Zolboo
import logo from './../logo.svg';
import { useEffect, useState } from 'react';
import 'bulma/css/bulma.min.css';
import authHeader from './../auth/authHeader'
import authService from './../auth/authService'

export default function LandingSearchPage(props) {
  const [loggedIn, changeLoggedIn] = useState(false);
  const [username, changeUsername] = useState(null);
  const [password, changePassword] = useState(null);
  useEffect(() => {
    var token = authHeader();
    //if it returns the token
    if (token["x-access-token"]) {
      changeLoggedIn(true);
    }
  }, []);
  function handleLogout() {
    //delete the local token
    authService.logout();
    //change the state
    changeLoggedIn(false);
  }
  return (
    <div className="Navbar">
      <div className="navbar" role="navigation" aria-label="main navigation">
        <div className="navbar-brand">
          <a className="navbar-item" href="/">
            <img src={logo} alt="logo" width="112" height="28"></img>
          </a>

          <a role="button" className="navbar-burger" aria-label="menu" aria-expanded="false" data-target="navbarBasicExample">
            <span aria-hidden="true"></span>
            <span aria-hidden="true"></span>
            <span aria-hidden="true"></span>
          </a>
        </div>

        <div id="navbarBasicExample" className="navbar-menu">
          <div className="navbar-start">
            <a className="navbar-item">
              Home
            </a>

            <a className="navbar-item">
              About
            </a>
          </div>
        </div>

        <div className="navbar-end">
          {loggedIn === false &&
            <div className="navbar-item">
              <div class="field">
                <div class="control">
                  <input class="input is-large" type="username" placeholder="Your Username" value={this.state.username} onChange={this.handleUsername} />
                </div>
              </div>

              <div class="field">
                <div class="control">
                  <input class="input is-large" type="password" placeholder="Your Password" value={this.state.password} onChange={this.handlePassword} />
                </div>
              </div>
              <div className="buttons">
                <a className="button is-primary">
                  <strong>Sign up</strong>
                </a>
                <a className="button is-light" href="/login">
                  Log in
                </a>
              </div>
            </div>
          }
          {loggedIn &&
            <div className="navbar-item">
              <div className="buttons">
                <a className="button is-primary" onClick={handleLogout}>
                  <strong>Logout</strong>
                </a>
              </div>
            </div>
          }
        </div>
      </div>
    </div>
  );
}
