import { GoogleMap, useLoadScript, Marker } from '@react-google-maps/api';
import mapStyles from './../mapStyles';
import React, { useState } from 'react';
import CustomNavbar from '../CustomNavbar';
import GooglePlacesAutocomplete from 'react-google-places-autocomplete';
import 'bulma/css/bulma.min.css';
import './AskLocation.css';
var CONFIG = require('./../../config.json');

//function that returns the custom map
const mapContainerStyle = {
    width: "100vw",
    height: "100vh"
}
const options = {
    styles: mapStyles,
    disableDefaultUI: true
}

var center = {
    lat: 38.8977,
    lng: 77.0365
}
export default function AskLocation(props) {
    const [selected, setSelected] = useState(null);
    //load the maps
    const libraries = ["places"];
    const { isLoaded, loadError } = useLoadScript({
        googleMapsApiKey: CONFIG.JS_MAPS_KEY,
        libraries
    });
    const [marker, setMarker] = React.useState();
    if (loadError) return "LoadError";
    if (!isLoaded) return "Loading";
    const savedLocation = localStorage.getItem("selectedLocation");
    if (savedLocation) {
        const split = savedLocation.split(' ')
        center.lat = split[0]
        center.lng = split[1]
    }
    return (
        <div>
            <CustomNavbar></CustomNavbar>
            <div className="hero is-fullheight-with-navbar">
                <div className="box" id="question">
                    <h1 class="title has-text-black">
                        Where would you like to play?
                    </h1>
                    <span>
                        <GooglePlacesAutocomplete
                            apiKey={CONFIG.PLACES_KEy}
                            selectProps={{
                                selected,
                                onChange: setSelected,
                            }}
                        />
                    </span>
                </div>
                {
                    marker &&
                    <div class="box" id="rightarrow">
                        <input
                            type="image"
                            alt="right arrow"
                            src={window.location.origin + "/svgs/rightarrow.svg"}
                            onClick={props.nextStep}>
                        </input>
                    </div>
                }
                <GoogleMap
                    mapContainerStyle={mapContainerStyle}
                    zoom={15}
                    center={center}
                    options={options}
                    onClick={
                        (event) => {
                            setMarker({
                                lat: event.latLng.lat(),
                                lng: event.latLng.lng()
                            });
                            //save the location into the parent's state
                            props.handleLocation(event.latLng.lat(), event.latLng.lng());
                        }
                    }
                >
                    {marker &&
                        <Marker position={{ lat: marker.lat, lng: marker.lng }}></Marker>
                    }
                </GoogleMap>
            </div>
        </div>
    );
}
